def swing_blades

  # Expected output:
  #  {
  #    "Joe Smith"=>{:email=>"joe@email.com", :address=>"123 Main st.", :phone=>"555-123-4567"},
  #    "Sally Johnson"=>{:email=>"sally@email.com", :address=>"404 Not Found Dr.",  :phone=>"123-234-3454"}
  #  }

  contacts = {
    "Joe Smith" => {

    },
    "Sally Johnson" => {}
  }
  contact_data = [
    ["joe@email.com", "123 Main st.", "555-123-4567"],
    ["sally@email.com", "404 Not Found Dr.", "123-234-3454"]
  ]

  #contact =
  #"Joe Smith" => {}

  contacts.each do |contact,v|
    #deriving first name for contact_data query
    first_name = contact.split(" ").first.downcase
    contact_data.each do |data|
      if data[0].downcase.include?(first_name)
        data.each_with_index do |specific_data, idx|
        contacts[contact][:email] = data[idx] if idx == 0
        contacts[contact][:address] = data[idx] if idx == 1
        contacts[contact][:phone] = data[idx] if idx == 2
        end
      end
    end
  end
  p contacts
end

